// Obtener la consulta del usuario
const userQuery = $('extrae mensaje').first().json.mensaje;

// Obtener datos del Google Sheets
const faqData = $input.all();

// Validar que hay datos
if (!faqData || faqData.length === 0) {
  return {
    json: {
      found: false,
      confidence: 0,
      pregunta: '',
      respuesta: 'No hay preguntas frecuentes disponibles en este momento.',
      score: 0
    }
  };
}

// Función para calcular similitud básica
function calculateSimilarity(str1, str2, options = {}) {
  // Configuración por defecto
  const {
    minWordLength = 3,
    caseSensitive = false
  } = options;

  // Validar tipos y strings vacíos
  if (typeof str1 !== 'string' || typeof str2 !== 'string') {
    throw new TypeError('Ambos argumentos deben ser strings');
  }

  if (!str1 || !str2) return 0;

  // Normalizar strings
  const normalizedStr1 = caseSensitive ? str1 : str1.toLowerCase();
  const normalizedStr2 = caseSensitive ? str2 : str2.toLowerCase();

  // Tokenizar y filtrar palabras
  const words1 = normalizedStr1
    .split(/\s+/)
    .filter(word => word.length > minWordLength);

  const words2 = normalizedStr2
    .split(/\s+/)
    .filter(word => word.length > minWordLength);

  // Evitar división por cero
  if (words1.length === 0 || words2.length === 0) return 0;

  // Usar Set para búsquedas más eficientes O(1) en lugar de O(n)
  const words2Set = new Set(words2);

  // Contar coincidencias únicas
  const uniqueMatches = new Set(
    words1.filter(word => words2Set.has(word))
  );

  const matches = uniqueMatches.size;

  // Calcular similitud (Jaccard-like pero usando max en lugar de unión)
  return matches / Math.max(words1.length, words2.length);
}
// Buscar mejor coincidencia
let bestMatch = null;
let bestScore = 0;

faqData.forEach(row => {
  const pregunta = row.json.descripcion || '';
  // Saltar filas vacías
  if (!pregunta) return;
  // Calcular similitud solo con la pregunta
  const score = calculateSimilarity(userQuery, pregunta);

  if (score > bestScore) {
    bestScore = score;
    bestMatch = row.json;
  }
});

// Retornar resultado
return {
  json: {
    found: bestScore > 0.3, // Umbral de confianza
    confidence: Math.round(bestScore * 100) / 100,
    preguntaEncontrada: bestMatch?.descripcion || '',
    respuesta: bestMatch?.url || 'No encontré un manual relacionado disculpa.',
    score: bestScore
  }
};
